import React, { useState, useEffect, useCallback } from 'react';
import { getAllUsers, createUser, updateUser, deleteUser } from '../../services/admin';

const AdminPanel = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isCreateModalOpen, setCreateModalOpen] = useState(false);
    const [isEditModalOpen, setEditModalOpen] = useState(false);
    const [selectedUser, setSelectedUser] = useState(null);
    const [formData, setFormData] = useState({ name: '', email: '', password: '', role: 'Job Seeker' });

    const fetchUsers = useCallback(async () => {
        try {
            setLoading(true);
            const response = await getAllUsers();
            setUsers(response.data || []);
        } catch (err) {
            setError(err.message || 'Failed to fetch users');
        } finally {
            setLoading(false);
        }
    }, []);

    useEffect(() => {
        fetchUsers();
    }, [fetchUsers]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleCreateUser = async (e) => {
        e.preventDefault();
        try {
            await createUser(formData);
            setCreateModalOpen(false);
            fetchUsers(); // Refresh user list
            setFormData({ name: '', email: '', password: '', role: 'Job Seeker' }); // Reset form
        } catch (err) {
            setError(err.response?.data?.error || err.message || 'Failed to create user');
        }
    };

    const handleUpdateUser = async (e) => {
        e.preventDefault();
        if (!selectedUser) return;
        try {
            const dataToUpdate = {};
            if (formData.email) dataToUpdate.email = formData.email;
            if (formData.password) dataToUpdate.password = formData.password;

            if (Object.keys(dataToUpdate).length === 0) {
                setError('No changes to submit.');
                return;
            }

            await updateUser(selectedUser._id, dataToUpdate);
            setEditModalOpen(false);
            fetchUsers(); // Refresh user list
        } catch (err) {
            setError(err.response?.data?.error || err.message || 'Failed to update user');
        }
    };

    const handleDeleteUser = async (userId) => {
        if (window.confirm('Are you sure you want to delete this user?')) {
            try {
                await deleteUser(userId);
                fetchUsers(); // Refresh user list
            } catch (err) {
                setError(err.response?.data?.error || err.message || 'Failed to delete user');
            }
        }
    };

    const openEditModal = (user) => {
        setSelectedUser(user);
        setFormData({ email: '', password: '' }); // Reset form for editing
        setEditModalOpen(true);
    };

    if (loading) return <div className="text-center py-8">Loading...</div>;

    return (
        <div className="p-6">
            <h1 className="text-3xl font-bold mb-6">Admin Panel - User Management</h1>
            {error && <div className="alert alert-error shadow-lg mb-4"><div><span>{error}</span><button className='btn btn-sm btn-ghost' onClick={() => setError(null)}>X</button></div></div>}
            <div className="mb-4">
                <button className="btn btn-primary" onClick={() => setCreateModalOpen(true)}>Create New User</button>
            </div>

            {/* User Table */}
            <div className="overflow-x-auto">
                <table className="table w-full">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Role</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user._id}>
                                <td>{user.name}</td>
                                <td>{user.email}</td>
                                <td>{user.role}</td>
                                <td className='space-x-2'>
                                    <button className="btn btn-sm btn-info" onClick={() => openEditModal(user)}>Edit</button>
                                    <button className="btn btn-sm btn-error" onClick={() => handleDeleteUser(user._id)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {/* Create User Modal */}
            {isCreateModalOpen && (
                <div className="modal modal-open">
                    <div className="modal-box">
                        <h3 className="font-bold text-lg">Create New User</h3>
                        <form onSubmit={handleCreateUser}>
                            <div className="form-control">
                                <label className="label"><span className="label-text">Name</span></label>
                                <input type="text" name="name" value={formData.name} onChange={handleInputChange} className="input input-bordered" required />
                            </div>
                            <div className="form-control">
                                <label className="label"><span className="label-text">Email</span></label>
                                <input type="email" name="email" value={formData.email} onChange={handleInputChange} className="input input-bordered" required />
                            </div>
                            <div className="form-control">
                                <label className="label"><span className="label-text">Password</span></label>
                                <input type="password" name="password" value={formData.password} onChange={handleInputChange} className="input input-bordered" required />
                            </div>
                            <div className="form-control">
                                <label className="label"><span className="label-text">Role</span></label>
                                <select name="role" value={formData.role} onChange={handleInputChange} className="select select-bordered">
                                    <option>Job Seeker</option>
                                    <option>Employer</option>
                                </select>
                            </div>
                            <div className="modal-action">
                                <button type="submit" className="btn btn-primary">Create</button>
                                <button type="button" className="btn" onClick={() => setCreateModalOpen(false)}>Cancel</button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

            {/* Edit User Modal */}
            {isEditModalOpen && selectedUser && (
                <div className="modal modal-open">
                    <div className="modal-box">
                        <h3 className="font-bold text-lg">Edit User: {selectedUser.name}</h3>
                        <form onSubmit={handleUpdateUser}>
                            <div className="form-control">
                                <label className="label"><span className="label-text">New Email (optional)</span></label>
                                <input type="email" name="email" placeholder="Enter new email" value={formData.email} onChange={handleInputChange} className="input input-bordered" />
                            </div>
                            <div className="form-control">
                                <label className="label"><span className="label-text">New Password (optional)</span></label>
                                <input type="password" name="password" placeholder="Enter new password" value={formData.password} onChange={handleInputChange} className="input input-bordered" />
                            </div>
                            <div className="modal-action">
                                <button type="submit" className="btn btn-primary">Update</button>
                                <button type="button" className="btn" onClick={() => setEditModalOpen(false)}>Cancel</button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AdminPanel;
