import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { getUserProfile } from '../../services/api';

const ProfileView = () => {
    const { user } = useAuth();
    const [profile, setProfile] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchProfile = async () => {
            if (!user?.id) {
                console.log('No user ID available');
                return;
            }

            console.log('Fetching profile for user:', user.id);

            try {
                setLoading(true);
                const profileData = await getUserProfile(user.id);
                console.log('Profile data received:', profileData);
                setProfile(profileData);
            } catch (err) {
                console.error('Profile fetch error:', err);
                setError('No profile found. Please create your profile.');
            } finally {
                setLoading(false);
            }
        };

        fetchProfile();
    }, [user]);

    if (loading) {
        return (
            <div className="flex justify-center items-center py-8">
                <div className="loading loading-spinner loading-lg"></div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="alert alert-warning">
                <svg xmlns="http://www.w3.org/2000/svg" className="stroke-current shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.96-.833-2.732 0L3.732 16c-.77.833.192 2.5 1.732 2.5z" />
                </svg>
                <span>{error}</span>
            </div>
        );
    }

    if (!profile) {
        return (
            <div className="alert alert-info">
                <svg xmlns="http://www.w3.org/2000/svg" className="stroke-current shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                <span>No profile found. Please create your profile to get started.</span>
            </div>
        );
    }

    return (
        <div className="space-y-6">
            {/* Header Card */}
            <div className="card bg-base-100 shadow-xl">
                <div className="card-body">
                    <div className="flex items-center space-x-4">
                        <div className="avatar placeholder">
                            <div className="bg-primary text-primary-content rounded-full w-16">
                                <span className="text-xl font-bold">
                                    {profile.full_name?.charAt(0)?.toUpperCase() || 'U'}
                                </span>
                            </div>
                        </div>
                        <div>
                            <h2 className="card-title text-2xl">{profile.full_name}</h2>
                            <p className="text-base-content/70">{user?.email}</p>
                            <div className="flex items-center gap-2 mt-1">
                                <div className={`badge ${profile.is_available ? 'badge-success' : 'badge-warning'}`}>
                                    {profile.is_available ? 'Available for work' : 'Not available'}
                                </div>
                                <div className="badge badge-outline">{user?.role}</div>
                            </div>
                        </div>
                    </div>
                    {profile.description && (
                        <p className="mt-4 text-base-content/80">{profile.description}</p>
                    )}
                </div>
            </div>

            {/* Contact Information */}
            <div className="card bg-base-100 shadow-xl">
                <div className="card-body">
                    <h3 className="card-title">Contact Information</h3>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        {profile.phone && (
                            <div>
                                <span className="font-semibold">Phone:</span>
                                <p>{profile.phone}</p>
                            </div>
                        )}
                        {profile.address && (
                            <div>
                                <span className="font-semibold">Address:</span>
                                <p>{profile.address}</p>
                            </div>
                        )}
                        {profile.age && (
                            <div>
                                <span className="font-semibold">Age:</span>
                                <p>{profile.age} years old</p>
                            </div>
                        )}
                        {profile.gender && (
                            <div>
                                <span className="font-semibold">Gender:</span>
                                <p>{profile.gender}</p>
                            </div>
                        )}
                    </div>
                </div>
            </div>

            {/* Skills */}
            {profile.skills && profile.skills.length > 0 && (
                <div className="card bg-base-100 shadow-xl">
                    <div className="card-body">
                        <h3 className="card-title">Skills</h3>
                        <div className="flex flex-wrap gap-2">
                            {profile.skills.map((skill, index) => (
                                <div key={index} className="badge badge-primary">{skill}</div>
                            ))}
                        </div>
                    </div>
                </div>
            )}

            {/* Experience & Education */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {/* Experience */}
                {profile.experience && (
                    <div className="card bg-base-100 shadow-xl">
                        <div className="card-body">
                            <h3 className="card-title">Experience</h3>
                            <p className="whitespace-pre-wrap">{profile.experience}</p>
                        </div>
                    </div>
                )}

                {/* Education */}
                {profile.education && (
                    <div className="card bg-base-100 shadow-xl">
                        <div className="card-body">
                            <h3 className="card-title">Education</h3>
                            <p className="whitespace-pre-wrap">{profile.education}</p>
                        </div>
                    </div>
                )}
            </div>

            {/* Certifications */}
            {profile.certifications && profile.certifications.length > 0 && (
                <div className="card bg-base-100 shadow-xl">
                    <div className="card-body">
                        <h3 className="card-title">Certifications</h3>
                        <div className="space-y-2">
                            {profile.certifications.map((cert, index) => (
                                <div key={index} className="p-3 bg-base-200 rounded-lg">
                                    {cert}
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ProfileView;
