import React, { useState, useEffect } from 'react';
import { getUserProfile, updateUserProfile, createUserProfile } from '../../services/api';
import { useAuth } from '../../context/AuthContext';

const ProfileEdit = () => {
    const { user } = useAuth();
    const [loading, setLoading] = useState(true);
    const [saving, setSaving] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [profile, setProfile] = useState(null);
    const [isNewProfile, setIsNewProfile] = useState(false);

    const [formData, setFormData] = useState({
        full_name: '',
        phone: '',
        address: '',
        age: '',
        gender: 'Male',
        description: '',
        skills: [],
        experience: '',
        education: '',
        certifications: [],
        is_available: true
    });

    useEffect(() => {
        if (user?.id) {
            fetchProfile();
        }
    }, [user]);

    const fetchProfile = async () => {
        try {
            setLoading(true);
            const profileData = await getUserProfile(user.id);
            setProfile(profileData);
            setFormData({
                full_name: profileData.full_name || '',
                phone: profileData.phone || '',
                address: profileData.address || '',
                age: profileData.age || '',
                gender: profileData.gender || 'Male',
                description: profileData.description || '',
                skills: profileData.skills || [],
                experience: profileData.experience || '',
                education: profileData.education || '',
                certifications: profileData.certifications || [],
                is_available: profileData.is_available !== undefined ? profileData.is_available : true
            });
            setIsNewProfile(false);
        } catch (err) {
            if (err.message.includes('not found')) {
                // Profile doesn't exist, prepare for creation
                setIsNewProfile(true);
                setFormData(prev => ({
                    ...prev,
                    full_name: user.name || ''
                }));
            } else {
                setError('Failed to load profile: ' + err.message);
            }
        } finally {
            setLoading(false);
        }
    };

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        if (type === 'checkbox') {
            setFormData({ ...formData, [name]: checked });
        } else {
            setFormData({ ...formData, [name]: value });
        }
        setError('');
        setSuccess('');
    };

    const handleArrayChange = (name, value) => {
        const array = value.split(',').map(item => item.trim()).filter(item => item);
        setFormData({ ...formData, [name]: array });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setSaving(true);
        setError('');
        setSuccess('');

        try {
            // Prepare data for submission
            const submitData = {
                ...formData,
                user_id: user.id,
                age: parseInt(formData.age) || 18,
                expected_salary_min: formData.expected_salary_min ? parseInt(formData.expected_salary_min) : null,
                expected_salary_max: formData.expected_salary_max ? parseInt(formData.expected_salary_max) : null
            };

            if (isNewProfile) {
                await createUserProfile(submitData);
                setSuccess('Profile created successfully!');
                setIsNewProfile(false);
            } else {
                await updateUserProfile(profile.id, submitData);
                setSuccess('Profile updated successfully!');
            }

            // Refresh profile data
            await fetchProfile();
        } catch (err) {
            setError('Failed to save profile: ' + err.message);
        } finally {
            setSaving(false);
        }
    };

    if (loading) {
        return (
            <div className="flex justify-center items-center h-64">
                <div className="loading loading-spinner loading-lg"></div>
            </div>
        );
    }

    return (
        <div className="max-w-4xl mx-auto p-6">
            <div className="card bg-base-100 shadow-xl">
                <div className="card-body">
                    <h2 className="card-title text-2xl mb-6">
                        {isNewProfile ? 'Create Profile' : 'Edit Profile'}
                    </h2>

                    {error && (
                        <div className="alert alert-error mb-4">
                            <svg xmlns="http://www.w3.org/2000/svg" className="stroke-current shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
                            </svg>
                            <span>{error}</span>
                        </div>
                    )}

                    {success && (
                        <div className="alert alert-success mb-4">
                            <svg xmlns="http://www.w3.org/2000/svg" className="stroke-current shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                            </svg>
                            <span>{success}</span>
                        </div>
                    )}

                    <form onSubmit={handleSubmit} className="space-y-6">
                        {/* Basic Information */}
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            <div className="form-control">
                                <label className="label">
                                    <span className="label-text font-semibold">Full Name *</span>
                                </label>
                                <input
                                    type="text"
                                    name="full_name"
                                    value={formData.full_name}
                                    onChange={handleChange}
                                    className="input input-bordered"
                                    required
                                />
                            </div>

                            <div className="form-control">
                                <label className="label">
                                    <span className="label-text font-semibold">Phone *</span>
                                </label>
                                <input
                                    type="tel"
                                    name="phone"
                                    value={formData.phone}
                                    onChange={handleChange}
                                    className="input input-bordered"
                                    placeholder="+6281234567890"
                                    required
                                />
                            </div>

                            <div className="form-control">
                                <label className="label">
                                    <span className="label-text font-semibold">Age *</span>
                                </label>
                                <input
                                    type="number"
                                    name="age"
                                    value={formData.age}
                                    onChange={handleChange}
                                    className="input input-bordered"
                                    min="18"
                                    max="100"
                                    required
                                />
                            </div>

                            <div className="form-control">
                                <label className="label">
                                    <span className="label-text font-semibold">Gender *</span>
                                </label>
                                <select
                                    name="gender"
                                    value={formData.gender}
                                    onChange={handleChange}
                                    className="select select-bordered"
                                    required
                                >
                                    <option value="Male">Male</option>
                                    <option value="Female">Female</option>
                                </select>
                            </div>
                        </div>

                        <div className="form-control">
                            <label className="label">
                                <span className="label-text font-semibold">Address</span>
                            </label>
                            <input
                                type="text"
                                name="address"
                                value={formData.address}
                                onChange={handleChange}
                                className="input input-bordered"
                                placeholder="Jakarta, Indonesia"
                            />
                        </div>

                        <div className="form-control">
                            <label className="label">
                                <span className="label-text font-semibold">Description</span>
                            </label>
                            <textarea
                                name="description"
                                value={formData.description}
                                onChange={handleChange}
                                className="textarea textarea-bordered h-24"
                                placeholder="Tell us about yourself..."
                            />
                        </div>

                        {/* Skills and Experience */}
                        <div className="form-control">
                            <label className="label">
                                <span className="label-text font-semibold">Skills</span>
                                <span className="label-text-alt">Separate with commas</span>
                            </label>
                            <input
                                type="text"
                                value={formData.skills.join(', ')}
                                onChange={(e) => handleArrayChange('skills', e.target.value)}
                                className="input input-bordered"
                                placeholder="JavaScript, React, Node.js"
                            />
                        </div>

                        <div className="form-control">
                            <label className="label">
                                <span className="label-text font-semibold">Work Experience</span>
                            </label>
                            <textarea
                                name="experience"
                                value={formData.experience}
                                onChange={handleChange}
                                className="textarea textarea-bordered h-24"
                                placeholder="Describe your work experience..."
                            />
                        </div>

                        <div className="form-control">
                            <label className="label">
                                <span className="label-text font-semibold">Education</span>
                            </label>
                            <textarea
                                name="education"
                                value={formData.education}
                                onChange={handleChange}
                                className="textarea textarea-bordered h-20"
                                placeholder="Your educational background..."
                            />
                        </div>

                        <div className="form-control">
                            <label className="label">
                                <span className="label-text font-semibold">Certifications</span>
                                <span className="label-text-alt">Separate with commas</span>
                            </label>
                            <input
                                type="text"
                                value={formData.certifications.join(', ')}
                                onChange={(e) => handleArrayChange('certifications', e.target.value)}
                                className="input input-bordered"
                                placeholder="AWS Certified Developer, Google Cloud Professional"
                            />
                        </div>

                        <div className="form-control">
                            <label className="label cursor-pointer justify-start gap-2">
                                <input
                                    type="checkbox"
                                    name="is_available"
                                    checked={formData.is_available}
                                    onChange={handleChange}
                                    className="checkbox checkbox-primary"
                                />
                                <span className="label-text font-semibold">Available for work</span>
                            </label>
                        </div>

                        <div className="card-actions justify-end pt-4">
                            <button
                                type="submit"
                                className={`btn btn-primary ${saving ? 'loading' : ''}`}
                                disabled={saving}
                            >
                                {saving ? 'Saving...' : (isNewProfile ? 'Create Profile' : 'Update Profile')}
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default ProfileEdit;
