import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import ProfileView from '../components/Profile/ProfileView';
import ProfileEdit from '../components/Profile/ProfileEdit';
import { toast } from 'react-toastify';

const Profile = () => {
    const [isEditing, setIsEditing] = useState(false);
    const location = useLocation();
    const navigate = useNavigate();

    // Check if redirected from job application with a message
    useEffect(() => {
        if (location.state?.message) {
            toast.info(location.state.message);
            // Auto switch to edit mode if redirected for profile creation
            setIsEditing(true);
        }
    }, [location.state]);

    // Handle success callback after profile creation/update
    const handleProfileSuccess = () => {
        setIsEditing(false);

        // If there was a return path (e.g., from job application), navigate back
        if (location.state?.returnPath) {
            navigate(location.state.returnPath);
        }
    };

    return (
        <div className="container mx-auto p-4 max-w-6xl">
            <div className="flex justify-between items-center mb-6">
                <h1 className="text-3xl font-bold">Profil Saya</h1>
                <button
                    onClick={() => setIsEditing(!isEditing)}
                    className={`btn ${isEditing ? 'btn-outline' : 'btn-primary'}`}
                >
                    {isEditing ? 'Batal' : 'Edit Profil'}
                </button>
            </div>

            {isEditing ? (
                <ProfileEdit onSuccess={handleProfileSuccess} />
            ) : (
                <ProfileView onEditClick={() => setIsEditing(true)} />
            )}
        </div>
    );
};

export default Profile;
