import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { createApplication } from '../../services/api';
import { useAuth } from '../../context/AuthContext';

const ApplicationForm = () => {
    const { id: jobPostId } = useParams();
    const navigate = useNavigate();
    const location = useLocation();
    const { user } = useAuth();
    const jobPost = location.state?.jobPost;

    const [formData, setFormData] = useState({
        job_post_id: jobPostId || '',
        cover_letter: '',
        cv_file: null,
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        // Redirect if user is not a jobseeker
        if (!user) {
            navigate('/login', { 
                state: { 
                    from: { pathname: `/jobs/${jobPostId}/apply` },
                    message: 'Please login to apply for this job'
                }
            });
            return;
        }

        if (user.role !== 'jobseeker') {
            navigate(`/jobs/${jobPostId}`, {
                state: { message: 'Only job seekers can apply for positions' }
            });
            return;
        }
    }, [user, jobPostId, navigate]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value,
        }));
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            // Validate file type
            const allowedTypes = ['application/pdf', 'application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'];
            if (!allowedTypes.includes(file.type)) {
                setError('Please upload a PDF or Word document');
                return;
            }

            // Validate file size (max 5MB)
            if (file.size > 5 * 1024 * 1024) {
                setError('File size must be less than 5MB');
                return;
            }

            setFormData(prev => ({
                ...prev,
                cv_file: file,
            }));
            setError('');
        }
    };

    const convertFileToBase64 = (file) => {
        return new Promise((resolve, reject) => {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onload = () => {
                // Remove the data:mime/type;base64, prefix
                const base64 = reader.result.split(',')[1];
                resolve(base64);
            };
            reader.onerror = error => reject(error);
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!user?.id) {
            setError('You must be logged in to submit an application');
            return;
        }

        if (!formData.job_post_id) {
            setError('Job post ID is required');
            return;
        }

        if (!formData.cv_file) {
            setError('Please upload your CV');
            return;
        }

        try {
            setLoading(true);
            setError('');

            // Convert CV file to base64
            const cvBase64 = await convertFileToBase64(formData.cv_file);

            const applicationData = {
                user_id: user.id,
                job_post_id: formData.job_post_id,
                cover_letter: formData.cover_letter.trim(),
                cv_data: cvBase64,
                cv_filename: formData.cv_file.name,
                cv_content_type: formData.cv_file.type,
                status: 'Pending'
            };

            await createApplication(applicationData);

            // Navigate back to job detail with success message
            navigate(`/jobs/${jobPostId}`, {
                state: { message: 'Application submitted successfully!' }
            });
        } catch (err) {
            setError(err.message || 'Failed to submit application');
        } finally {
            setLoading(false);
        }
    };

    const handleBack = () => {
        navigate(`/jobs/${jobPostId}`);
    };

    // Don't render if user is not authenticated or not a jobseeker
    if (!user || user.role !== 'jobseeker') {
        return null;
    }

    return (
        <div className="container mx-auto p-4 max-w-4xl">
            <div className="mb-6">
                <button 
                    onClick={handleBack}
                    className="btn btn-ghost btn-sm mb-4"
                >
                    ← Back to Job Details
                </button>
            </div>

            {/* Job Info Card */}
            {jobPost && (
                <div className="card bg-base-100 shadow-lg mb-6">
                    <div className="card-body">
                        <h2 className="card-title text-xl">Applying for:</h2>
                        <div className="grid md:grid-cols-2 gap-4 mt-2">
                            <div>
                                <h3 className="font-bold text-lg">{jobPost.title}</h3>
                                <p className="text-base-content/70">{jobPost.company} • {jobPost.location}</p>
                                <div className="flex gap-2 mt-2">
                                    <div className="badge badge-secondary">{jobPost.job_type}</div>
                                    <div className="badge badge-outline">
                                        ${jobPost.salary_min?.toLocaleString()} - ${jobPost.salary_max?.toLocaleString()}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )}

            <div className="card bg-base-100 shadow-xl">
                <div className="card-body">
                    <h2 className="card-title text-2xl mb-6">Submit Your Application</h2>

                    {error && (
                        <div className="alert alert-error mb-4">
                            <svg xmlns="http://www.w3.org/2000/svg" className="stroke-current shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
                            </svg>
                            <span>{error}</span>
                        </div>
                    )}

                    <form onSubmit={handleSubmit} className="space-y-6">
                        {/* Cover Letter */}
                        <div className="form-control">
                            <label className="label">
                                <span className="label-text">Cover Letter</span>
                                <span className="label-text-alt">Optional</span>
                            </label>
                            <textarea
                                name="cover_letter"
                                value={formData.cover_letter}
                                onChange={handleChange}
                                className="textarea textarea-bordered h-32 w-full"
                                placeholder="Tell us why you're interested in this position and what makes you a great fit..."
                                disabled={loading}
                            ></textarea>
                            <label className="label">
                                <span className="label-text-alt">This will help your application stand out</span>
                            </label>
                        </div>

                        {/* CV Upload */}
                        <div className="form-control">
                            <label className="label">
                                <span className="label-text">Upload CV/Resume *</span>
                            </label>
                            <input
                                type="file"
                                onChange={handleFileChange}
                                className="file-input file-input-bordered w-full"
                                accept=".pdf,.doc,.docx"
                                required
                                disabled={loading}
                            />
                            <label className="label">
                                <span className="label-text-alt">Accepted formats: PDF, DOC, DOCX (Max 5MB)</span>
                            </label>
                            {formData.cv_file && (
                                <div className="mt-2 p-2 bg-base-200 rounded">
                                    <span className="text-sm">
                                        📄 {formData.cv_file.name} ({(formData.cv_file.size / 1024 / 1024).toFixed(2)} MB)
                                    </span>
                                </div>
                            )}
                        </div>

                        {/* Action Buttons */}
                        <div className="flex gap-4 justify-end">
                            <button
                                type="button"
                                onClick={handleBack}
                                className="btn btn-outline"
                                disabled={loading}
                            >
                                Cancel
                            </button>
                            <button
                                type="submit"
                                className={`btn btn-primary ${loading ? 'loading' : ''}`}
                                disabled={loading}
                            >
                                {loading ? 'Submitting...' : 'Submit Application'}
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default ApplicationForm;
