import React, { useEffect, useState } from 'react';
import { getApplicationsByUser, getJobPost } from '../../services/api';
import { useAuth } from '../../context/AuthContext';

const ApplicationList = () => {
    const { user } = useAuth();
    const [applications, setApplications] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchApplications = async () => {
            if (!user || !user._id) return;
            setLoading(true);
            setError(null);
            try {
                const data = await getApplicationsByUser(user._id);
                // Ambil detail job post untuk setiap application
                const appsWithJob = await Promise.all(
                    (data.items || []).map(async (app) => {
                        let jobTitle = '';
                        let company = '';
                        try {
                            const job = await getJobPost(app.job_post_id);
                            jobTitle = job.title;
                            company = job.company;
                        } catch {
                            jobTitle = `Job #${app.job_post_id}`;
                            company = '-';
                        }
                        return {
                            ...app,
                            job_post_title: jobTitle,
                            job_post_company: company
                        };
                    })
                );
                setApplications(appsWithJob);
            } catch (err) {
                setError(err.message || 'Failed to fetch applications');
            } finally {
                setLoading(false);
            }
        };
        fetchApplications();
    }, [user]);

    if (loading) return <div className="text-center py-8">Loading...</div>;
    if (error) return <div className="text-center text-red-500 py-8">{error}</div>;

    return (
        <div className="overflow-x-auto">
            <h2 className="text-2xl font-bold mb-4">My Applications</h2>
            {applications.length === 0 ? (
                <div className="text-center py-8 text-gray-500">No applications found.</div>
            ) : (
                <table className="table w-full">
                    <thead>
                        <tr>
                            <th>Job Title</th>
                            <th>Company</th>
                            <th>Status</th>
                            <th>CV</th>
                            <th>Applied At</th>
                        </tr>
                    </thead>
                    <tbody>
                        {applications.map(app => (
                            <tr key={app._id}>
                                <td>{app.job_post_title}</td>
                                <td>{app.job_post_company}</td>
                                <td>{app.status}</td>
                                <td>{app.cv_filename ? <a href="#" className="text-blue-600 underline">{app.cv_filename}</a> : '-'}</td>
                                <td>{app.created_at ? new Date(app.created_at).toLocaleDateString() : '-'}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default ApplicationList;
