import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { jobPostService } from '../../services/jobPosts';

const JobCreateForm = () => {
    const { user } = useAuth();
    const navigate = useNavigate();
      const [formData, setFormData] = useState({
        title: '',
        company: '',
        location: '',
        job_type: 'Full-time',
        description: '',
        requirements: '',
        salary_min: '',
        salary_max: '',
        contact_email: '',
        image_data: null,
        image_filename: null,
        image_content_type: null,
        is_active: true
    });
    
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const jobTypes = ['Full-time', 'Part-time', 'Freelance', 'Other'];

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));
    };    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!user || user.role !== 'Employer') {
            setError('Only employers can create job posts');
            return;
        }

        setLoading(true);
        setError(null);

        try {
            // Prepare data for submission according to the database model
            const jobData = {
                user_id: user.id,
                title: formData.title,
                company: formData.company,
                location: formData.location,
                job_type: formData.job_type,
                description: formData.description,
                requirements: formData.requirements.split('\n').map(req => req.trim()).filter(req => req),
                salary_min: formData.salary_min ? parseInt(formData.salary_min) : 0,
                salary_max: formData.salary_max ? parseInt(formData.salary_max) : 0,
                contact_email: formData.contact_email,
                image_data: formData.image_data,
                image_filename: formData.image_filename,
                image_content_type: formData.image_content_type,
                is_active: formData.is_active
            };

            await jobPostService.create(jobData);
            navigate('/my-jobs', { 
                state: { 
                    message: 'Job post created successfully!' 
                } 
            });
        } catch (error) {
            console.error('Error creating job post:', error);
            setError(error.response?.data?.detail || 'Failed to create job post');
        } finally {
            setLoading(false);
        }
    };

    if (!user || user.role !== 'Employer') {
        return (
            <div className="container mx-auto px-4 py-8">
                <div className="alert alert-error">
                    <span>Access denied. Only employers can create job posts.</span>
                </div>
            </div>
        );
    }

    return (
        <div className="container mx-auto px-4 py-8 max-w-4xl">
            <div className="mb-6">
                <h1 className="text-3xl font-bold mb-2">Create New Job Post</h1>
                <p className="text-gray-600">Fill in the details to post a new job opening</p>
            </div>

            {error && (
                <div className="alert alert-error mb-6">
                    <span>{error}</span>
                </div>
            )}

            <form onSubmit={handleSubmit} className="space-y-6">
                <div className="card bg-base-100 shadow-lg">
                    <div className="card-body">
                        <h2 className="card-title mb-4">Basic Information</h2>
                        
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            <div className="form-control md:col-span-2">
                                <label className="label">
                                    <span className="label-text">Job Title *</span>
                                </label>
                                <input
                                    type="text"
                                    name="title"
                                    value={formData.title}
                                    onChange={handleChange}
                                    className="input input-bordered"
                                    required
                                    placeholder="e.g. Senior Software Engineer"
                                />
                            </div>

                            <div className="form-control">
                                <label className="label">
                                    <span className="label-text">Department *</span>
                                </label>
                                <input
                                    type="text"
                                    name="department"
                                    value={formData.department}
                                    onChange={handleChange}
                                    className="input input-bordered"
                                    required
                                    placeholder="e.g. Engineering"
                                />
                            </div>

                            <div className="form-control">
                                <label className="label">
                                    <span className="label-text">Location *</span>
                                </label>
                                <input
                                    type="text"
                                    name="location"
                                    value={formData.location}
                                    onChange={handleChange}
                                    className="input input-bordered"
                                    required
                                    placeholder="e.g. New York, NY"
                                />
                            </div>

                            <div className="form-control">
                                <label className="label">
                                    <span className="label-text">Job Type *</span>
                                </label>
                                <select
                                    name="job_type"
                                    value={formData.job_type}
                                    onChange={handleChange}
                                    className="select select-bordered"
                                    required
                                >
                                    {jobTypes.map(type => (
                                        <option key={type} value={type}>{type}</option>
                                    ))}
                                </select>
                            </div>

                            <div className="form-control">
                                <label className="label">
                                    <span className="label-text">Experience Level *</span>
                                </label>
                                <select
                                    name="experience_level"
                                    value={formData.experience_level}
                                    onChange={handleChange}
                                    className="select select-bordered"
                                    required
                                >
                                    {experienceLevels.map(level => (
                                        <option key={level} value={level}>{level}</option>
                                    ))}
                                </select>
                            </div>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
                            <div className="form-control">
                                <label className="label">
                                    <span className="label-text">Minimum Salary</span>
                                </label>
                                <input
                                    type="number"
                                    name="salary_min"
                                    value={formData.salary_min}
                                    onChange={handleChange}
                                    className="input input-bordered"
                                    placeholder="e.g. 70000"
                                />
                            </div>

                            <div className="form-control">
                                <label className="label">
                                    <span className="label-text">Maximum Salary</span>
                                </label>
                                <input
                                    type="number"
                                    name="salary_max"
                                    value={formData.salary_max}
                                    onChange={handleChange}
                                    className="input input-bordered"
                                    placeholder="e.g. 90000"
                                />
                            </div>
                        </div>

                        <div className="form-control">
                            <label className="label">
                                <span className="label-text">Application Deadline</span>
                            </label>
                            <input
                                type="date"
                                name="application_deadline"
                                value={formData.application_deadline}
                                onChange={handleChange}
                                className="input input-bordered"
                            />
                        </div>

                        <div className="form-control">
                            <label className="cursor-pointer label justify-start">
                                <input
                                    type="checkbox"
                                    name="is_remote"
                                    checked={formData.is_remote}
                                    onChange={handleChange}
                                    className="checkbox checkbox-primary mr-3"
                                />
                                <span className="label-text">Remote work available</span>
                            </label>
                        </div>
                    </div>
                </div>

                <div className="card bg-base-100 shadow-lg">
                    <div className="card-body">
                        <h2 className="card-title mb-4">Job Details</h2>
                        
                        <div className="form-control">
                            <label className="label">
                                <span className="label-text">Job Description *</span>
                            </label>
                            <textarea
                                name="description"
                                value={formData.description}
                                onChange={handleChange}
                                className="textarea textarea-bordered h-32"
                                required
                                placeholder="Describe the role, responsibilities, and what the candidate will be doing..."
                            />
                        </div>

                        <div className="form-control">
                            <label className="label">
                                <span className="label-text">Requirements *</span>
                            </label>
                            <textarea
                                name="requirements"
                                value={formData.requirements}
                                onChange={handleChange}
                                className="textarea textarea-bordered h-32"
                                required
                                placeholder="List the qualifications, experience, and skills required..."
                            />
                        </div>

                        <div className="form-control">
                            <label className="label">
                                <span className="label-text">Skills Required</span>
                                <span className="label-text-alt">Separate with commas</span>
                            </label>
                            <textarea
                                name="skills_required"
                                value={formData.skills_required}
                                onChange={handleChange}
                                className="textarea textarea-bordered"
                                placeholder="JavaScript, React, Node.js, Python..."
                            />
                        </div>

                        <div className="form-control">
                            <label className="label">
                                <span className="label-text">Benefits</span>
                                <span className="label-text-alt">Separate with commas</span>
                            </label>
                            <textarea
                                name="benefits"
                                value={formData.benefits}
                                onChange={handleChange}
                                className="textarea textarea-bordered"
                                placeholder="Health insurance, 401k, Flexible hours, Remote work..."
                            />
                        </div>
                    </div>
                </div>

                <div className="flex gap-4 justify-end">
                    <button
                        type="button"
                        onClick={() => navigate('/my-jobs')}
                        className="btn btn-ghost"
                        disabled={loading}
                    >
                        Cancel
                    </button>
                    <button
                        type="submit"
                        className="btn btn-primary"
                        disabled={loading}
                    >
                        {loading ? (
                            <>
                                <span className="loading loading-spinner loading-sm"></span>
                                Creating...
                            </>
                        ) : (
                            'Create Job Post'
                        )}
                    </button>
                </div>
            </form>
        </div>
    );
};

export default JobCreateForm;
