import React, { useEffect, useState } from 'react';
import jobPostService from '../../services/jobPosts';

const JobPostList = () => {
    const [jobPosts, setJobPosts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchJobPosts = async () => {
            try {
                const data = await jobPostService.getJobPosts();
                setJobPosts(data.jobs || data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchJobPosts();
    }, []);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div className="p-4">
            <h1 className="text-2xl font-bold mb-4">Job Posts</h1>
            <ul className="space-y-4">
                {jobPosts.map((post) => (
                    <li key={post.id} className="border p-4 rounded shadow">
                        <h2 className="text-xl font-semibold">{post.title}</h2>
                        <p>{post.description}</p>
                        <a href={`/jobs/${post.id}`} className="text-blue-500 hover:underline">
                            View Details
                        </a>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default JobPostList;
