import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { createApplication } from '../../services/api';
import { useAuth } from '../../context/AuthContext';

const ApplicationForm = () => {
    const { id: jobPostId } = useParams();
    const navigate = useNavigate();
    const location = useLocation();
    const { user } = useAuth();
    const jobPost = location.state?.jobPost;

    const [formData, setFormData] = useState({
        job_post_id: jobPostId || '',
        cover_letter: '',
        cv_file: null,
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        // Redirect if user is not a jobseeker
        if (!user) {
            navigate('/login', { 
                state: { 
                    from: { pathname: `/jobs/${jobPostId}/apply` },
                    message: 'Please login to apply for this job'
                }
            });
            return;
        }

        if (user.role !== 'jobseeker') {
            navigate(`/jobs/${jobPostId}`, {
                state: { message: 'Only job seekers can apply for positions' }
            });
            return;
        }
    }, [user, jobPostId, navigate]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value,
        }));
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            // Validate file type
            const allowedTypes = ['application/pdf', 'application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'];
            if (!allowedTypes.includes(file.type)) {
                setError('Please upload a PDF or Word document');
                return;
            }

            // Validate file size (max 5MB)
            if (file.size > 5 * 1024 * 1024) {
                setError('File size must be less than 5MB');
                return;
            }

            setFormData(prev => ({
                ...prev,
                cv_file: file,
            }));
            setError('');
        }
    };

    const convertFileToBase64 = (file) => {
        return new Promise((resolve, reject) => {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onload = () => {
                // Remove the data:mime/type;base64, prefix
                const base64 = reader.result.split(',')[1];
                resolve(base64);
            };
            reader.onerror = error => reject(error);
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!user?.id) {
            setError('You must be logged in to submit an application');
            return;
        }

        if (!formData.job_post_id) {
            setError('Job post ID is required');
            return;
        }

        if (!formData.cv_file) {
            setError('Please upload your CV');
            return;
        }

        try {
            setLoading(true);
            setError('');

            // Convert CV file to base64
            const cvBase64 = await convertFileToBase64(formData.cv_file);

            const applicationData = {
                user_id: user.id,
                job_post_id: formData.job_post_id,
                cover_letter: formData.cover_letter.trim(),
                cv_data: cvBase64,
                cv_filename: formData.cv_file.name,
                cv_content_type: formData.cv_file.type,
                status: 'Pending'
            };

            await createApplication(applicationData);

            if (onSuccess) {
                onSuccess();
            }
        } catch (err) {
            setError(err.message || 'Failed to submit application');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="card bg-base-100 shadow-xl">
            <div className="card-body">
                <h2 className="card-title text-2xl mb-6">Submit Application</h2>

                {error && (
                    <div className="alert alert-error mb-4">
                        <svg xmlns="http://www.w3.org/2000/svg" className="stroke-current shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
                        </svg>
                        <span>{error}</span>
                    </div>
                )}

                <form onSubmit={handleSubmit} className="space-y-6">
                    {/* Job Post ID (if not provided as prop) */}
                    {!jobPostId && (
                        <div className="form-control">
                            <label className="label">
                                <span className="label-text">Job Post ID *</span>
                            </label>
                            <input
                                type="text"
                                name="job_post_id"
                                value={formData.job_post_id}
                                onChange={handleChange}
                                className="input input-bordered w-full"
                                placeholder="Enter job post ID"
                                required
                                disabled={loading}
                            />
                        </div>
                    )}

                    {/* Cover Letter */}
                    <div className="form-control">
                        <label className="label">
                            <span className="label-text">Cover Letter</span>
                        </label>
                        <textarea
                            name="cover_letter"
                            value={formData.cover_letter}
                            onChange={handleChange}
                            className="textarea textarea-bordered h-32 w-full"
                            placeholder="Write a compelling cover letter to introduce yourself and explain why you're interested in this position..."
                            disabled={loading}
                        />
                        <label className="label">
                            <span className="label-text-alt">Optional but recommended</span>
                        </label>
                    </div>

                    {/* CV Upload */}
                    <div className="form-control">
                        <label className="label">
                            <span className="label-text">Upload CV/Resume *</span>
                        </label>
                        <input
                            type="file"
                            onChange={handleFileChange}
                            className="file-input file-input-bordered w-full"
                            accept=".pdf,.doc,.docx"
                            required
                            disabled={loading}
                        />
                        <label className="label">
                            <span className="label-text-alt">Accepted formats: PDF, DOC, DOCX (Max 5MB)</span>
                        </label>
                        {formData.cv_file && (
                            <div className="mt-2 p-2 bg-base-200 rounded">
                                <span className="text-sm">
                                    📄 {formData.cv_file.name} ({(formData.cv_file.size / 1024 / 1024).toFixed(2)} MB)
                                </span>
                            </div>
                        )}
                    </div>

                    {/* Action Buttons */}
                    <div className="flex gap-4 justify-end">
                        {onCancel && (
                            <button
                                type="button"
                                onClick={onCancel}
                                className="btn btn-outline"
                                disabled={loading}
                            >
                                Cancel
                            </button>
                        )}
                        <button
                            type="submit"
                            className={`btn btn-primary ${loading ? 'loading' : ''}`}
                            disabled={loading}
                        >
                            {loading ? 'Submitting...' : 'Submit Application'}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default ApplicationForm;
