import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getJobPost } from '../../services/api';
import { useAuth } from '../../context/AuthContext';

const JobPostDetail = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { user } = useAuth();
    const [jobPost, setJobPost] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchJobPost();
    }, [id]);

    const fetchJobPost = async () => {
        try {
            setLoading(true);
            setError(null);
            const data = await getJobPost(id);
            setJobPost(data);
        } catch (err) {
            setError(err.message || 'Failed to fetch job details');
            console.error('Error fetching job post:', err);
        } finally {
            setLoading(false);
        }
    };

    const handleApply = () => {
        if (!user) {
            navigate('/login', { 
                state: { 
                    from: { pathname: `/jobs/${id}` },
                    message: 'Please login to apply for this job'
                }
            });
            return;
        }
        // Navigate to application form with job post data
        navigate(`/jobs/${id}/apply`, { state: { jobPost } });
    };

    const handleBack = () => {
        navigate(-1);
    };

    if (loading) {
        return (
            <div className="container mx-auto p-4 max-w-4xl">
                <div className="flex justify-center items-center h-64">
                    <div className="loading loading-spinner loading-lg"></div>
                </div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="container mx-auto p-4 max-w-4xl">
                <div className="alert alert-error">
                    <svg xmlns="http://www.w3.org/2000/svg" className="stroke-current shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
                    </svg>
                    <span>Error: {error}</span>
                    <div>
                        <button className="btn btn-sm btn-ghost" onClick={fetchJobPost}>
                            Try Again
                        </button>
                    </div>
                </div>
            </div>
        );
    }

    if (!jobPost) {
        return (
            <div className="container mx-auto p-4 max-w-4xl">
                <div className="text-center py-12">
                    <div className="text-6xl mb-4">❌</div>
                    <h3 className="text-xl font-semibold mb-2">Job Not Found</h3>
                    <p className="text-base-content/70">The job post you're looking for doesn't exist.</p>
                    <button className="btn btn-primary mt-4" onClick={handleBack}>
                        Go Back
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className="container mx-auto p-4 max-w-4xl">
            {/* Header */}
            <div className="mb-6">
                <button 
                    onClick={handleBack}
                    className="btn btn-ghost btn-sm mb-4"
                >
                    ← Back to Jobs
                </button>

                <div className="card bg-base-100 shadow-xl">
                    <div className="card-body">
                        <div className="flex flex-col lg:flex-row lg:justify-between lg:items-start gap-4">
                            <div className="flex-1">
                                <h1 className="text-3xl font-bold mb-2">{jobPost.title}</h1>
                                <div className="flex flex-wrap gap-2 mb-4">
                                    <div className="badge badge-secondary">{jobPost.job_type}</div>
                                    <div className={`badge ${jobPost.is_active ? 'badge-success' : 'badge-error'}`}>
                                        {jobPost.is_active ? 'Active' : 'Inactive'}
                                    </div>
                                </div>
                                <div className="text-lg text-base-content/70 mb-2">
                                    <span className="font-semibold">{jobPost.company}</span> • {jobPost.location}
                                </div>
                                <div className="text-xl font-bold text-primary">
                                    ${jobPost.salary_min?.toLocaleString()} - ${jobPost.salary_max?.toLocaleString()}
                                </div>
                            </div>

                            <div className="flex flex-col gap-2">
                                <button 
                                    onClick={handleApply}
                                    className="btn btn-primary btn-lg"
                                    disabled={!jobPost.is_active}
                                >
                                    Apply Now
                                </button>
                                <div className="text-sm text-base-content/60 text-center">
                                    Posted on {new Date(jobPost.created_at).toLocaleDateString()}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Job Details */}
            <div className="grid gap-6 lg:grid-cols-3">
                {/* Main Content */}
                <div className="lg:col-span-2 space-y-6">
                    {/* Description */}
                    <div className="card bg-base-100 shadow-lg">
                        <div className="card-body">
                            <h2 className="card-title">Job Description</h2>
                            <div className="prose max-w-none">
                                <p className="text-base-content/80 whitespace-pre-line">
                                    {jobPost.description}
                                </p>
                            </div>
                        </div>
                    </div>

                    {/* Requirements */}
                    {jobPost.requirements && jobPost.requirements.length > 0 && (
                        <div className="card bg-base-100 shadow-lg">
                            <div className="card-body">
                                <h2 className="card-title">Requirements</h2>
                                <ul className="list-disc list-inside space-y-2">
                                    {jobPost.requirements.map((req, index) => (
                                        <li key={index} className="text-base-content/80">
                                            {req}
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        </div>
                    )}

                    {/* Responsibilities */}
                    {jobPost.responsibilities && jobPost.responsibilities.length > 0 && (
                        <div className="card bg-base-100 shadow-lg">
                            <div className="card-body">
                                <h2 className="card-title">Responsibilities</h2>
                                <ul className="list-disc list-inside space-y-2">
                                    {jobPost.responsibilities.map((resp, index) => (
                                        <li key={index} className="text-base-content/80">
                                            {resp}
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        </div>
                    )}

                    {/* Benefits */}
                    {jobPost.benefits && jobPost.benefits.length > 0 && (
                        <div className="card bg-base-100 shadow-lg">
                            <div className="card-body">
                                <h2 className="card-title">Benefits</h2>
                                <ul className="list-disc list-inside space-y-2">
                                    {jobPost.benefits.map((benefit, index) => (
                                        <li key={index} className="text-base-content/80">
                                            {benefit}
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        </div>
                    )}
                </div>

                {/* Sidebar */}
                <div className="space-y-6">
                    {/* Job Summary */}
                    <div className="card bg-base-100 shadow-lg">
                        <div className="card-body">
                            <h3 className="card-title">Job Summary</h3>
                            <div className="space-y-3">
                                <div>
                                    <div className="font-semibold">Company</div>
                                    <div className="text-base-content/70">{jobPost.company}</div>
                                </div>
                                <div>
                                    <div className="font-semibold">Location</div>
                                    <div className="text-base-content/70">{jobPost.location}</div>
                                </div>
                                <div>
                                    <div className="font-semibold">Job Type</div>
                                    <div className="text-base-content/70">{jobPost.job_type}</div>
                                </div>
                                <div>
                                    <div className="font-semibold">Experience Level</div>
                                    <div className="text-base-content/70">{jobPost.experience_level || 'Not specified'}</div>
                                </div>
                                <div>
                                    <div className="font-semibold">Application Deadline</div>
                                    <div className="text-base-content/70">
                                        {jobPost.application_deadline 
                                            ? new Date(jobPost.application_deadline).toLocaleDateString()
                                            : 'Open until filled'
                                        }
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Company Info */}
                    <div className="card bg-base-100 shadow-lg">
                        <div className="card-body">
                            <h3 className="card-title">About the Company</h3>
                            <div className="space-y-3">
                                <div>
                                    <div className="font-semibold">{jobPost.company}</div>
                                    <div className="text-base-content/70">
                                        {jobPost.company_description || 'No company description available.'}
                                    </div>
                                </div>
                                {jobPost.company_website && (
                                    <div>
                                        <div className="font-semibold">Website</div>
                                        <a 
                                            href={jobPost.company_website} 
                                            target="_blank" 
                                            rel="noopener noreferrer"
                                            className="link link-primary"
                                        >
                                            {jobPost.company_website}
                                        </a>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>

                    {/* Apply Again Button */}
                    <div className="card bg-primary text-primary-content shadow-lg">
                        <div className="card-body text-center">
                            <h3 className="card-title justify-center">Interested?</h3>
                            <p className="text-sm opacity-90">
                                Don't miss this opportunity!
                            </p>
                            <button 
                                onClick={handleApply}
                                className="btn btn-secondary"
                                disabled={!jobPost.is_active}
                            >
                                Apply Now
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default JobPostDetail;
