import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { createApplication, getUserProfile } from '../../services/api';
import { useAuth } from '../../context/AuthContext';

const ApplicationForm = () => {
    const { id: jobPostId } = useParams();
    const navigate = useNavigate();
    const location = useLocation();
    const { user } = useAuth();
    const jobPost = location.state?.jobPost;

    const [formData, setFormData] = useState({
        job_post_id: jobPostId || '',
        cover_letter: '',
        cv_file: null,
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [profile, setProfile] = useState(null);
    const [loadingProfile, setLoadingProfile] = useState(true);

    useEffect(() => {
        // Redirect if user is not a jobseeker
        if (!user) {
            navigate('/login', { 
                state: { 
                    from: { pathname: `/jobs/${jobPostId}/apply` },
                    message: 'Silakan login untuk melamar pekerjaan ini'
                }
            });
            return;
        }

        if (user.role !== 'Job Seeker') {
            navigate(`/jobs/${jobPostId}`, {
                state: { message: 'Hanya pencari kerja yang dapat melamar posisi' }
            });
            return;
        }

        // Fetch user profile
        const fetchUserProfile = async () => {
            setLoadingProfile(true);
            try {
                const profileData = await getUserProfile(user._id);
                setProfile(profileData);
            } catch (err) {
                console.error('Error fetching profile:', err);
                // If profile not found, redirect to profile page
                if (err.message.includes('not found') || err.message.includes('Profile not found')) {
                    navigate('/profile', { 
                        state: { 
                            message: 'Silakan lengkapi profil Anda terlebih dahulu sebelum melamar pekerjaan',
                            returnPath: `/jobs/${jobPostId}/apply`
                        } 
                    });
                }
            } finally {
                setLoadingProfile(false);
            }
        };

        fetchUserProfile();
    }, [user, jobPostId, navigate]);

    const handleBack = () => {
        navigate(`/jobs/${jobPostId}`);
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            // Check file size (5MB limit)
            if (file.size > 5 * 1024 * 1024) {
                setError('File size must be less than 5MB');
                return;
            }

            // Check file type
            const allowedTypes = ['application/pdf', 'application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'];
            if (!allowedTypes.includes(file.type)) {
                setError('Please upload a PDF, DOC, or DOCX file');
                return;
            }

            setError('');
            setFormData({ ...formData, cv_file: file });
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        if (!profile) {
            setError('Profil tidak ditemukan. Silakan lengkapi profil Anda terlebih dahulu.');
            setLoading(false);
            return;
        }

        try {
            // Convert CV file to base64
            const cvFileBase64 = await fileToBase64(formData.cv_file);

            // Prepare application data
            const applicationData = {
                user_id: user._id,
                job_post_id: parseInt(formData.job_post_id),
                cv_data: cvFileBase64,
                cv_filename: formData.cv_file.name,
                cv_content_type: formData.cv_file.type
            };

            await createApplication(applicationData);

            // Navigate back to job detail with success message
            navigate(`/jobs/${jobPostId}`, {
                state: { 
                    message: 'Lamaran berhasil dikirim!',
                    messageType: 'success'
                }
            });
        } catch (err) {
            console.error('Application submission error:', err);
            setError(err.message || 'Gagal mengirim lamaran. Silakan coba lagi.');
        } finally {
            setLoading(false);
        }
    };

    // Helper function to convert file to base64
    const fileToBase64 = (file) => {
        return new Promise((resolve, reject) => {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onload = () => {
                // Remove the data URL prefix (e.g., "data:application/pdf;base64,")
                const base64String = reader.result.split(',')[1];
                resolve(base64String);
            };
            reader.onerror = (error) => reject(error);
        });
    };

    // Don't render if user is not authenticated or not a jobseeker
    if (!user || user.role !== 'Job Seeker') {
        return null;
    }

    // Show loading state while fetching profile
    if (loadingProfile) {
        return (
            <div className="container mx-auto p-4 max-w-4xl">
                <div className="flex justify-center items-center h-64">
                    <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary"></div>
                </div>
            </div>
        );
    }

    // Don't render if profile doesn't exist
    if (!profile) {
        return null;
    }

    return (
        <div className="container mx-auto p-4 max-w-4xl">
            <div className="mb-6">
                <button 
                    onClick={handleBack}
                    className="btn btn-ghost btn-sm mb-4"
                >
                    ← Kembali ke Detail Pekerjaan
                </button>
            </div>

            {jobPost && (
                <div className="card bg-base-100 shadow-lg mb-6">
                    <div className="card-body">
                        <h2 className="card-title text-xl">Melamar untuk:</h2>
                        <h3 className="font-bold text-lg">{jobPost.title}</h3>
                        <p className="text-base-content/70">{jobPost.company} • {jobPost.location}</p>
                    </div>
                </div>
            )}

            {/* Profile Card */}
            <div className="card bg-base-100 shadow-lg mb-6">
                <div className="card-body">
                    <h2 className="card-title text-xl">Profil Anda</h2>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-2">
                        <div>
                            <p className="font-medium text-gray-700">Nama Lengkap</p>
                            <p className="font-bold">{profile.full_name}</p>
                        </div>
                        <div>
                            <p className="font-medium text-gray-700">Nomor Telepon</p>
                            <p>{profile.phone}</p>
                        </div>
                        <div>
                            <p className="font-medium text-gray-700">Usia</p>
                            <p>{profile.age} tahun</p>
                        </div>
                        <div>
                            <p className="font-medium text-gray-700">Jenis Kelamin</p>
                            <p>{profile.gender}</p>
                        </div>
                    </div>
                    <div className="mt-3">
                        <p className="font-medium text-gray-700">Keahlian</p>
                        <div className="flex flex-wrap gap-2 mt-1">
                            {profile.skills && profile.skills.map((skill, index) => (
                                <span key={index} className="badge badge-primary badge-outline">
                                    {skill}
                                </span>
                            ))}
                        </div>
                    </div>
                </div>
            </div>

            <div className="card bg-base-100 shadow-xl">
                <div className="card-body">
                    <h2 className="card-title text-2xl mb-6">Submit Your Application</h2>

                    {error && (
                        <div className="alert alert-error mb-4">
                            <svg xmlns="http://www.w3.org/2000/svg" className="stroke-current shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
                            </svg>
                            <span>{error}</span>
                        </div>
                    )}

                    <form onSubmit={handleSubmit} className="space-y-6">
                        {/* Cover Letter */}
                        <div className="form-control">
                            <label className="label">
                                <span className="label-text font-medium">Cover Letter *</span>
                            </label>
                            <textarea
                                className="textarea textarea-bordered h-32"
                                placeholder="Tell us why you're the perfect fit for this position..."
                                value={formData.cover_letter}
                                onChange={(e) => setFormData({...formData, cover_letter: e.target.value})}
                                required
                            />
                        </div>

                        {/* CV Upload */}
                        <div className="form-control">
                            <label className="label">
                                <span className="label-text font-medium">Upload CV/Resume *</span>
                            </label>
                            <input
                                type="file"
                                className="file-input file-input-bordered w-full"
                                accept=".pdf,.doc,.docx"
                                onChange={handleFileChange}
                                required
                            />
                            <label className="label">
                                <span className="label-text-alt">Accepted formats: PDF, DOC, DOCX (max 5MB)</span>
                            </label>
                        </div>

                        {/* Submit Button */}
                        <div className="form-control mt-8">
                            <button
                                type="submit"
                                className={`btn btn-primary btn-lg w-full ${loading ? 'loading' : ''}`}
                                disabled={loading}
                            >
                                {loading ? 'Submitting Application...' : 'Submit Application'}
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default ApplicationForm;
