import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getUserProfile, getUser } from '../../services/api';

const JobseekerProfile = () => {
    const { userId } = useParams();
    const navigate = useNavigate();
    const [profile, setProfile] = useState(null);
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProfile = async () => {
            setLoading(true);
            setError(null);
            try {
                const userData = await getUser(userId);
                setUser(userData);
                const profileData = await getUserProfile(userId);
                setProfile(profileData);
            } catch (err) {
                setError(err.message || 'Failed to fetch profile');
            } finally {
                setLoading(false);
            }
        };
        fetchProfile();
    }, [userId]);

    if (loading) return <div className="text-center py-8">Loading...</div>;
    if (error) return <div className="text-center text-red-500 py-8">{error}</div>;
    if (!profile || !user) return <div className="text-center py-8 text-gray-500">Profile not found.</div>;

    return (
        <div className="max-w-xl mx-auto bg-white shadow rounded p-6 mt-6">
            <h2 className="text-2xl font-bold mb-4">Jobseeker Profile</h2>
            <div className="mb-2"><b>Name:</b> {user.name}</div>
            <div className="mb-2"><b>Email:</b> {user.email}</div>
            <div className="mb-2"><b>Phone:</b> {profile.phone}</div>
            <div className="mb-2"><b>Address:</b> {profile.address}</div>
            <div className="mb-2"><b>Education:</b> {profile.education}</div>
            <div className="mb-2"><b>Experience:</b> {profile.experience}</div>
            <div className="mb-2"><b>Skills:</b> {profile.skills}</div>
            <button className="btn btn-outline mt-4" onClick={() => navigate(-1)}>Back</button>
        </div>
    );
};

export default JobseekerProfile;
