import React, { useEffect, useState } from 'react';
import { getApplications, getApplicationsByUser, getJobPost } from '../../services/api';
import { useAuth } from '../../context/AuthContext';

const ApplicationList = () => {
    const [applications, setApplications] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [pagination, setPagination] = useState({
        page: 1,
        limit: 10,
        total: 0
    });

    const { user } = useAuth();

    useEffect(() => {
        if (user) {
            console.log('Current user:', user);
            // Check if user ID exists in expected format (_id or id)
            const userId = user._id || user.id;
            console.log('User ID used for fetching applications:', userId);

            if (userId) {
                fetchApplications();
            } else {
                console.error('Missing user ID! User object:', user);
                setError('User ID not found. Please try logging in again.');
            }
        }
    }, [pagination.page, user]);

    // For debugging purposes
    useEffect(() => {
        console.log('Current applications data:', applications);
    }, [applications]);

    const fetchApplications = async () => {
        try {
            setLoading(true);
            let data;

            if (user?.role === 'Admin') {
                // Admin can see all applications
                data = await getApplications({
                    skip: (pagination.page - 1) * pagination.limit,
                    limit: pagination.limit
                });
            } else if (user) {
                // Get userId from either _id or id property
                const userId = user._id || user.id;
                if (!userId) {
                    throw new Error('User ID not found');
                }

                // Regular users can only see their own applications
                data = await getApplicationsByUser(userId, {
                    skip: (pagination.page - 1) * pagination.limit,
                    limit: pagination.limit
                });
                console.log('Raw applications data received:', data);
            }

            if (data) {
                // Ensure we have an items array, even if it's empty
                const items = Array.isArray(data.items) ? data.items : 
                             (Array.isArray(data) ? data : []);

                console.log('Items to process:', items);

                // Fetch job post details for each application
                const applicationsWithJobInfo = await Promise.all(
                    items.map(async (application) => {
                        try {
                            const jobPost = await getJobPost(application.job_post_id);
                            return {
                                ...application,
                                job_post_title: jobPost.title,
                                job_post_company: jobPost.company
                            };
                        } catch (err) {
                            console.error(`Error fetching job post ${application.job_post_id}:`, err);
                            return {
                                ...application,
                                job_post_title: `Job #${application.job_post_id}`,
                                job_post_company: 'Unknown Company'
                            };
                        }
                    })
                );

                console.log('Processed applications with job info:', applicationsWithJobInfo);
                setApplications(applicationsWithJobInfo);
                setPagination(prev => ({
                    ...prev,
                    total: data.total || items.length || 0
                }));
            } else {
                // Handle case when no data is returned
                console.warn('No data returned from applications API');
                setApplications([]);
                setPagination(prev => ({
                    ...prev,
                    total: 0
                }));
            }
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const getStatusBadgeClass = (status) => {
        switch (status) {
            case 'Pending':
                return 'badge badge-warning';
            case 'Accepted':
                return 'badge badge-success';
            case 'Rejected':
                return 'badge badge-error';
            default:
                return 'badge badge-ghost';
        }
    };

    if (loading) return (
        <div className="flex justify-center items-center h-64">
            <div className="loading loading-spinner loading-lg"></div>
        </div>
    );

    if (error) return (
        <div className="alert alert-error">
            <svg xmlns="http://www.w3.org/2000/svg" className="stroke-current shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <div>
                <h3 className="font-bold">Error loading applications</h3>
                <div className="text-sm">{error}</div>
                <button 
                    className="btn btn-sm btn-outline mt-2"
                    onClick={() => {
                        setError(null);
                        fetchApplications();
                    }}
                >
                    Try Again
                </button>
            </div>
        </div>
    );

    return (
        <div className="space-y-4">
            <div className="flex justify-between items-center">
                <h2 className="text-2xl font-bold">My Applications</h2>
                <div className="text-sm text-base-content/70">
                    Total: {pagination.total} applications
                </div>
            </div>

            {applications.length === 0 ? (
                <div className="text-center py-8">
                    <p className="text-base-content/70">No job applications yet.</p>
                </div>
            ) : (
                <div className="overflow-x-auto">
                    <table className="table table-zebra w-full">
                        <thead>
                            <tr>
                                <th>Job</th>
                                <th>Status</th>
                                <th>CV</th>
                                <th>Application Date</th>
                            </tr>
                        </thead>
                        <tbody>
                            {applications.map((application, index) => (
                                <tr key={application._id || `app-${index}`}>
                                    <td className="font-medium">
                                        {application.job_post_title ? (
                                            <div>
                                                <div>{application.job_post_title}</div>
                                                <div className="text-sm text-gray-500">{application.job_post_company}</div>
                                            </div>
                                        ) : (
                                            `Job #${application.job_post_id}`
                                        )}
                                    </td>
                                    <td>
                                        <span className={getStatusBadgeClass(application.status)}>
                                            {application.status}
                                        </span>
                                    </td>
                                    <td>
                                        {application.cv_filename ? (
                                            <button 
                                                className="btn btn-sm btn-outline"
                                                onClick={() => {
                                                    // Here you would implement CV download
                                                    alert('CV download functionality would be implemented here');
                                                }}
                                            >
                                                ðŸ“„ {application.cv_filename}
                                            </button>
                                        ) : (
                                            <span className="text-base-content/50">No CV</span>
                                        )}
                                    </td>
                                    <td>
                                        {application.created_at ? new Date(application.created_at).toLocaleDateString() : 'Unknown date'}
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}

            {/* Pagination */}
            {pagination.total > pagination.limit && (
                <div className="flex justify-center">
                    <div className="join">                                <button 
                                className="join-item btn"
                                disabled={pagination.page === 1}
                                onClick={() => setPagination(prev => ({ ...prev, page: prev.page - 1 }))}
                            >
                                Previous
                            </button>
                            <button className="join-item btn btn-active">
                                Page {pagination.page}
                            </button>
                            <button 
                                className="join-item btn"
                                disabled={pagination.page * pagination.limit >= pagination.total}
                                onClick={() => setPagination(prev => ({ ...prev, page: prev.page + 1 }))}
                            >
                                Next
                            </button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ApplicationList;
